import React, {useEffect, useState} from 'react';
import axios from "axios";
import {useParams} from 'react-router-dom'
import {Row, Col} from "react-bootstrap";

const Meal = () => {

    const [food, setFood] = useState({});
    const params = useParams();
    useEffect(()=>{
        axios(`https://www.themealdb.com/api/json/v1/1/lookup.php?i=${params.id}`).then(({data})=> setFood(data.meals[0]))
    },[]);
    return (
        <>
            <Row>
                <Col md={6}>
                    <img style={{width: 100 + '%'}} src={food.strMealThumb} alt="food image"/>
                </Col>
                <Col md={6}>
                    <ul>
                        <li>Название: {food.strMeal}</li>
                        <li>Категория: {food.strCategory}</li>
                        <li>Страна: {food.strArea}</li>
                    </ul>
                    <iframe className='food__video' src={`https://www`}/>
                </Col>
            </Row>
        </>
    );
};

export default Meal;
