import React, {useEffect, useState} from 'react';
import axios from "axios";
import {Button, Card, Col, Container, Row} from "react-bootstrap";

const Category = () => {
    const [meal, setMeal] = useState([]);
    useEffect(()=>{
        axios('https://www.themealdb.com/api/json/v1/1/filter.php?c=chicken')
            .then(({data})=> setMeal(data.meals))
    },[]);
    return (
        <div>
            <Container>
                <Row>
                    {categories.map((item)=>(
                        <Col md={4} sm={6} xs={12} key={item.id}>
                            <Card >
                                <Card.Img variant="top" src={`${item.strCategoryThumb}`} />
                                <Card.Body>
                                    <Card.Title>{item.strCategory}</Card.Title>
                                    <Card.Text>
                                        {item.strCategoryDescription }
                                    </Card.Text>
                                    <Button variant="primary" type='button' onClick={()=> navigate(`category/${item.strCategory.toLowerCase()}`)}>Go somewhere</Button>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </Container>
        </div>
    );
};

export default Category;
